import{
    FETCH_WATCHES_START,
    FETCH_WATCHES_SUCCESS,
    FETCH_WATCHES_ERROR,
} from 'actionTypes'
import {fetchWatches as fetchWatchesApi} from 'api'

export const fetchWatches = () => async dispatch => {
    dispatch({
        type: FETCH_WATCHES_START
    })

    try{
        const watches = await fetchWatchesApi()
        dispatch({
            type: FETCH_WATCHES_SUCCESS,
            payload: watches
        })
    } catch(e){
        dispatch({
            type: FETCH_WATCHES_ERROR,
            payload: e,
            error: true
        })
    }
}