import R from 'ramda'

import watches from './mockWatches'
import categories from './mockCategories'

export const fetchWatches = async () => {
    return new Promise(resolve => {
        resolve(watches)
    })
}

export const fetchWatchById = async (id) => {
    return new Promise((resolve, reject) => {
        const watch = R.find(R.propEq('id', id), watches)
        resolve(watch)
    })
}

export const fetchCategories = async (id) => {
    return new Promise(resolve => {
        resolve(categories)
    })
}