import React from 'react'
import {Link} from 'react-router'
import {connect} from 'react-redux'

import {
    getTotalBasketCount,
    getTotalBasketPrice
} from 'selectors'

const BasketCart = ({totalBasketCount, totalPrice}) => (
    <div className='sidebar'>
            <Link
            to='/basket'
            id='dLabel'
            className='sidebar_link'
            >
                <div>
                    <img src="/images/icons8-buying-filled-50bldack.png" alt="Basket"/>
                </div>
                <div>
                    <p>Предметов в корзине - {totalBasketCount}</p>
                    <p>Цена предметов - {totalPrice} руб.</p>
                </div>
            </Link>
    </div>
)

const mapStateToProps = state => ({
    totalBasketCount: getTotalBasketCount(state),
    totalPrice: getTotalBasketPrice(state)
})

export default connect(mapStateToProps, null)(BasketCart)