import React, { Component } from 'react'

class Footer extends Component {
    constructor(props) {
        super(props);
        this.state = { email: '' }
        this.handleEmailChange = this.handleEmailChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleEmailChange(event) {
        this.setState({ email: event.target.value })
    }

    handleSubmit() {
        alert(`На E-mail ${this.state.email} оформлена подписка`)
        this.setState({ email: '' })
    }

    render() {
        return (
            <div className='footer'>
                <div className='container'>
                    <div className="row">
                        <div className="col-md-4 footer-element">
                            Рассылка новостей:
                            <div className="news-mailing">
                                <form 
                                onSubmit={this.handleSubmit} 
                                className='formFooter'
                                action="email.php" 
                                method="post"
                                >
                                    <input
                                        type="text"
                                        name='email'
                                        placeholder='Введите свой E-mail'
                                        onChange={this.handleEmailChange}
                                        value={this.state.email}
                                        pattern='^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$'
                                        required
                                    />
                                    <button>
                                        <img src="/images/icons8-gmail-filled-50.png" alt="Send" />
                                    </button>
                                </form>
                            </div>
                        </div>
                        <div className="col-md-4 footer-element">
                            Мы в соцсетях:
                            <div className='social'>
                                <a href="https://vk.com/ormedia_by"><img src="/images/icons8-vk-circled-filled-50.png" alt="VK" /></a>
                                <a href="https://web.facebook.com/ormedia.by/"><img src="/images/icons8-facebook-circled-filled-50.png" alt="Facebook" /></a>
                                <a href="https://twitter.com/"><img src="/images/icons8-twitter-circled-filled-50.png" alt="Twitter" /></a>
                                <a href="https://www.instagram.com/ormedia.by/"><img src="/images/icons8-instagram-filled-50.png" alt="Instagram" /></a>
                            </div>
                        </div>
                        <div className="col-md-4 footer-element">
                            Контактная информация:
                            <div className='contacts'>
                                <a href="tel:+375447075587">+375(44)707-55-87</a><br />
                                <a href="tel:+375447075587">+375(44)707-55-87</a>
                                <p>watchstore.by@gmail.com</p>
                                <p>г.Минск, ул.Мележа,1, оф.1223</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Footer