import React from 'react'
import {connect} from 'react-redux'
import R from 'ramda'

import {
    getBasketWatchesWithCount,
    getTotalBasketPrice
} from 'selectors'
import Footer from 'components/footer';
import Header from 'components/header';

const Basket = ({watches, totalPrice}) => {
    const isBasketEmpty = R.isEmpty(watches)
    console.log(watches.length)
  
    const renderContent = () => {
      return (
        <div>
          {isBasketEmpty && <div>Ваша корзина пуста</div>}
  
          <div className='basket-list'>
            <table className='basket-list-table'>
              <tbody>
              {watches.map((watch, index) => (
                <tr
                  key={index}
                  className='basket-list-table-item'
                >
                  <td className='first-table-column'>
                    <img
                      className='img-thumbnail'
                      src={watch.image}
                      alt={watch.name}
                    />
                  </td>
                  <td>{watch.name}</td>
                  <td>{watch.price} руб.</td>
                  <td className='count-basket-item'>{watch.count}</td>
                  <td className='delete-btn'>
                      <img src="/images/icons8-cancel-50.png" alt="Close"/>
                  </td>
                </tr>
              ))}
              </tbody>
            </table>
          </div>
          {
            R.not(isBasketEmpty) &&
            <div className='row'>
              <div className='total-prise'>
                Итого к оплате: {totalPrice} руб.
              </div>
            </div>
          }
        </div>
      )
    }
  
    const renderSidebar = () => (
      <div>
       Sidebar
      </div>
    )
  
    return (
      <div className='view-container'>
        <Header />
        <div className='container'>
          <div className='row flex-row-reverse'>
            <div className='col-md-3 btn-user-checkout'>
              {renderSidebar()}
            </div>
            <div className='col-md-9'>
              {renderContent()}
            </div>
          </div>
        </div>
        <Footer />
      </div>
    )
}  

const mapStateToProps = state => {
    return {
      watches: getBasketWatchesWithCount(state),
      totalPrice: getTotalBasketPrice(state)
    }
  }
  
  export default connect(mapStateToProps, null)(Basket)
  
