import React, {Component} from 'react'
import {connect} from 'react-redux'
import {Link} from 'react-router'

import {fetchWatches, addWatchToBasket} from 'actions'
import {getWatches} from 'selectors'

class Watches extends Component {
  componentDidMount () {
    this.props.fetchWatches()
  }

  renderWatch(watch, index) {
    const {addWatchToBasket} = this.props

    return(
      <div className=" col-md-3 col-sm-4 inLine-list" key={index}>
        <div className="thumbnail">
          <img 
          className="img-thumbnail" 
          src={watch.image} 
          alt={watch.name}
          />
          <div className="caption">
            <h4>
              <Link to={`/watches/${watch.id}`}>{watch.name}</Link>
            </h4>
            <span className="prise">
              <h4>{watch.price}руб.</h4>
              </span>
              <button 
              className="buyBtn"
              onClick={() => addWatchToBasket(watch.id)}
              >Купить</button>
          </div>
        </div> 
      </div> 
    )
  }

  render () {
    const {watches} = this.props
    return (
      <div>
        <div className="row inLine">
          {watches.map((watch, index) => this.renderWatch(watch, index))}
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  watches: getWatches(state)
})

const mapDispatchToProps = {
  fetchWatches,
  addWatchToBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Watches)
