import R from 'ramda'

import {
  ADD_WATCH_TO_BASKET,
  REMOVE_WATCH_FROM_BASKET,
  CLEAN_BASKET
} from 'actionTypes'

const initialState = []

export default (state = initialState, { type, payload }) => {
  switch (type) {
    case ADD_WATCH_TO_BASKET:
      return R.append(payload, state)
    case REMOVE_WATCH_FROM_BASKET:
      return R.without([payload], state)
    case CLEAN_BASKET:
      return []
    default:
      return state
  }
}