import R from 'ramda'
import {FETCH_WATCHES_SUCCESS, SEARCH_WATCH} from 'actionTypes'

const initialState = {
    ids: [],
    search: ''
}

export default (state = initialState, {type, payload}) => {
    switch(type){
        case FETCH_WATCHES_SUCCESS:
            return R.merge(state, {
                ids: R.pluck('id', payload)
            })
            case SEARCH_WATCH:
                return R.merge(state, {
                search: payload      
            })
        default:
            return state
    }
}